CREATE DATABASE HotelManagement;
USE HotelManagement;

CREATE TABLE Hotel (
    HotelID VARCHAR(20) PRIMARY KEY, 
    Name NVARCHAR(255) NOT NULL,
    Address NVARCHAR(255) NOT NULL,
    Phone NVARCHAR(10) CHECK (Phone LIKE '[0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9]'),
    Email NVARCHAR(255) CHECK (Email LIKE '%@%' AND Email LIKE '%.%'),
    Stars INT CHECK(Stars BETWEEN 0 AND 5),
    CheckinTime TIME NOT NULL,
    CheckoutTime TIME NOT NULL
);


CREATE TABLE Staff (
    StaffID VARCHAR(20) PRIMARY KEY,
    HotelID VARCHAR(20) DEFAULT 'H001',
    FirstName NVARCHAR(50) NOT NULL,
    LastName NVARCHAR(50) NOT NULL,
    Position NVARCHAR(100) NOT NULL,
    Salary DECIMAL(10,2) CHECK (Salary >= 0),
    DateOfBirth DATE,
    Address NVARCHAR(255),
    City NVARCHAR(100),
    Country NVARCHAR(100),
    Phone NVARCHAR(10) CHECK (Phone LIKE '[0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9]'),
    Email NVARCHAR(255) CHECK (Email LIKE '%@%' AND Email LIKE '%.%'),
    HireDate DATE DEFAULT GETDATE()
);

CREATE TABLE Guest (
    GuestID INT IDENTITY(1,1) PRIMARY KEY,
    FirstName NVARCHAR(50) NOT NULL,
    LastName NVARCHAR(50) NOT NULL,
    DateOfBirth DATE,
    Address NVARCHAR(255),
    City NVARCHAR(100),
    Country NVARCHAR(100),
    Phone NVARCHAR(10) CHECK (Phone LIKE '[0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9]'),
    Email NVARCHAR(255) CHECK (Email LIKE '%@%' AND Email LIKE '%.%')
);

CREATE TABLE Account (
    AccountID INT IDENTITY(1,1) PRIMARY KEY NOT NULL,
    Username VARCHAR(50) UNIQUE NOT NULL,
    Password VARCHAR(255) NOT NULL,   
    Role VARCHAR(50) NOT NULL CHECK (Role IN ('Admin', 'Staff', 'Guest')) DEFAULT 'Guest',
    CreatedAt DATE DEFAULT GETDATE(),
    StaffID VARCHAR(20) NULL,
    GuestID INT NULL,
    FOREIGN KEY (StaffID) REFERENCES Staff(StaffID) ON DELETE SET NULL,
    FOREIGN KEY (GuestID) REFERENCES Guest(GuestID) ON DELETE SET NULL
);


ALTER TABLE Account
ADD CONSTRAINT CHK_Account_Role CHECK (
    (Role = 'Staff' AND StaffID IS NOT NULL AND GuestID IS NULL) OR
    (Role = 'Guest' AND (GuestID IS NOT NULL OR GuestID IS NULL) AND StaffID IS NULL) OR
    (Role = 'Admin' AND StaffID IS NULL AND GuestID IS NULL)
);



CREATE TABLE RoomType (
    TypeID INT IDENTITY(1,1) PRIMARY KEY,
    Name NVARCHAR(50) NOT NULL,
    Description NVARCHAR(255),
    PricePerNight DECIMAL(10,2) NOT NULL,
    Capacity INT NOT NULL CHECK(Capacity >=1), -- số lượng người trong phòng
	ImageURL NVARCHAR(max) NOT NULL,
	Area DECIMAL(10,2) NOT NULL,
	Amenities NVARCHAR(50) NOT NULL
);


CREATE TABLE Room (
    RoomID VARCHAR(20) PRIMARY KEY,
    HotelID VARCHAR(20),
    TypeID INT NOT NULL,
    Status NVARCHAR(20) NOT NULL DEFAULT 'Available' CHECK (Status IN ('Available', 'Occupied', 'Reserved', 'Cleaning', 'Maintenance')),
    FOREIGN KEY (HotelID) REFERENCES Hotel(HotelID) ON DELETE CASCADE,
    FOREIGN KEY (TypeID) REFERENCES RoomType(TypeID) ON DELETE CASCADE
);


CREATE TABLE RoomImages ( --Lưu ảnh mô tả phòng
    ImageID INT IDENTITY(1,1) PRIMARY KEY,  -- Tự động tăng từ 1, mỗi lần tăng 1
    RoomID VARCHAR(20) NOT NULL,
    ImageURL NVARCHAR(max) NOT NULL,
    Description NVARCHAR(255) NULL,  
    FOREIGN KEY (RoomID) REFERENCES Room(RoomID) ON DELETE CASCADE
);



CREATE TABLE Booking (
    BookingID INT IDENTITY(1,1) PRIMARY KEY, -- T? ??ng t?ng ID
    GuestID INT NOT NULL,
    RoomID VARCHAR(20) NOT NULL,
    CheckinDate DATE NOT NULL,
    CheckoutDate DATE NOT NULL,
    TotalPrice DECIMAL(10,2) NOT NULL CHECK (TotalPrice >= 0), 
	Status TINYINT CHECK (Status IN (0,1,2,3,4,5,6)),
    FOREIGN KEY (GuestID) REFERENCES Guest(GuestID),
    FOREIGN KEY (RoomID) REFERENCES Room(RoomID)
);

