CREATE TABLE Service(
	ServiceID INT  IDENTITY(1,1) PRIMARY KEY,
	Name NVARCHAR(100) NOT NULL,
	Description NVARCHAR(500),
	BasePrice DECIMAL(10,2) NOT NULL CHECK (BasePrice >=0),
	ServiceType NVARCHAR(50) NOT NULL CHECK (ServiceType IN('Food','Spa', 'Transport', 'Entertainment','Others')),
	IsActive BIT DEFAULT 1
);

CREATE TABLE ServiceCombo(
	ComboID INT IDENTITY(1,1) PRIMARY KEY,
	Name NVARCHAR(100) NOT NULL,
	Description NVARCHAR(500),
	ComboPrice DECIMAL(10,2) NOT NULL CHECK (ComboPrice >= 0),
	IsActive BIT DEFAULT 1
);

CREATE TABLE ComboServiceDetails(
	DetailID INT IDENTITY(1,1) PRIMARY KEY,
	ComboID INT NOT NULL,
	ServiceID INT NOT NULL,
	Quantity INT NOT NULL CHECK (Quantity >=1),
	Foreign key (ComboID) references ServiceCombo(ComboID) on delete Cascade,
	Foreign key (ServiceID) references Service(ServiceID) on delete Cascade
);

CREATE TABLE BookingService(
	BookingServiceID INT IDENTITY(1,1) PRIMARY KEY,
	BookingID INT NOT NUll,
	ServiceID INT NULL,
	ComboID INT NULL,
	Quantity INT NOT NULL CHECK (Quantity>=1),
	PriceBooking DECIMAL(10,2) NOT NULL CHECK (PriceBooking >=0),
	ServiceDate Date NoT NULL,
	Status NVARCHAR(20) NOT NULL CHECK (Status IN('Pending','Confirmed','Completed','Canceled')) DEFAULT 'Confirmed',
	
	FOREIGN KEY (BookingID) REFERENCES Booking(BookingID) ON DELETE CASCADE,
    FOREIGN KEY (ServiceID) REFERENCES Service(ServiceID) ON DELETE SET NULL,
    FOREIGN KEY (ComboID) REFERENCES ServiceCombo(ComboID) ON DELETE SET NULL,
    
    -- Constraint đảm bảo chỉ có ServiceID hoặc ComboID được điền (không cả hai, không cả rỗng)
    CONSTRAINT CHK_ServiceOrCombo CHECK (
        (ServiceID IS NOT NULL AND ComboID IS NULL) OR 
        (ServiceID IS NULL AND ComboID IS NOT NULL)
    )

);

CREATE TABLE Payment (
    PaymentID INT IDENTITY(1,1) PRIMARY KEY,
    BookingID INT,
    Amount DECIMAL(10,2) NOT NULL,
    PaymentDate DATE NOT NULL DEFAULT GETDATE(),
    PaymentMethod VARCHAR(50) NOT NULL CHECK(PaymentMethod IN ('Cash', 'Credit Card', 'Debit Card', 'Online Payment')),
    FOREIGN KEY (BookingID) REFERENCES Booking(BookingID)
);

CREATE TRIGGER trg_Auto_StaffID
ON Staff
INSTEAD OF INSERT
AS
BEGIN
    DECLARE @newID VARCHAR(20)
    DECLARE @maxNum INT

    -- Lấy số lớn nhất hiện có trong StaffID, ví dụ S005 → 5
    SELECT @maxNum = MAX(CAST(SUBSTRING(StaffID, 2, LEN(StaffID)) AS INT)) FROM Staff

    -- Nếu bảng rỗng, bắt đầu từ 0
    IF @maxNum IS NULL SET @maxNum = 0

    -- Tăng lên 1 và tạo ID mới
    SET @newID = 'S' + RIGHT('000' + CAST(@maxNum + 1 AS VARCHAR(3)), 3)

    -- Thêm bản ghi mới với StaffID mới
    INSERT INTO Staff (StaffID, HotelID, FirstName, LastName, Position, Salary, DateOfBirth, Address, City, Country, Phone, Email, HireDate)
    SELECT @newID, HotelID, FirstName, LastName, Position, Salary, DateOfBirth, Address, City, Country, Phone, Email, HireDate
    FROM inserted
END


